import React from 'react'
import "./FormStyles.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";

const schema = yup.object().shape({
  fullName: yup.string().required,
  email: yup.string().email().required,
  subject: yup.string().required,
  message: yup.string().required
})

const Form = () => {

  const { register, handleSubmit, errors } = useForm({
    resolver: yupResolver(schema)
  });

  const submitForm = (data) => {

  }
  return (
    <div className="form">
        <form onSubmit={handleSubmit(submitForm)}>
            <label>Your Full Name</label>
            <input type="text" name="fullName" placeholder="First & Last Name" ref={register} />
            <p> {errors.fullName?.message} </p>
            <label>Your Email</label>
            <input type="email" name="email" ref={register} /><p> {errors.am?.message} </p>
            <label>Subject</label>
            <input type="text" name="subject" ref={register} />
            <p> {errors.fullName?.message} </p>
            <label>Message</label>
            <textarea rows="7" name="message" placeholder="Let me know how we can connect" ref={register} />
            <p> {errors.fullName?.message} </p>
            {/* <input type="submit" id="submit"/> */}
            <button className="btn">Submit</button>
        </form>
    </div>
  )
}

export default Form
