import project1 from "../assets/tic-tac-toe.png"
import project2 from "../assets/kingtutstomb.png"
import project3 from "../assets/rubyverse.png"

const projectCardData = [
    {
        imgsrc: project1,
        title: "Tic Tac Toe",
        text: "This Game will be updated upon completion. Right now the Draw Outcome logic is kicking my butt. The winner is announced correctly for either player. Also the squares nicely flash when they are pushed.",
        link: "puturlhere.com"
    },
    {
        imgsrc: project2,
        title: "King Tut's Tomb",
        text: "This Game is all about searching for the treasure without dying, click each square with caution.",
        link: "https://mikesc27.github.io/"
    },
    {
        imgsrc: project3,
        title: "Rubyverse",
        text: "This is a text based game where you answer Ruby questions as you move through the Ruby Mansion.",
        link: "puturlhere.com"
    },
];

export default projectCardData;
